import React, { useState } from 'react';



const Home = () => {

  // get set function using useState function!
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = () => {
    if (input.trim()) {
      setMessages([...messages, { text: input, sender: 'user' }]);
      // Simulate a response from the "AI"

      setTimeout(() => {
        setMessages(prevMessages => [ // set prevMessage is a list [...prevMessage, {value to set}]
          ...prevMessages, // previous state of message before update 
          // ... is spread operator to create new array that contain all elements of prevMessages array
          { text: `You said: ${input}`, sender: 'bot' }
        ]);
      }, 1000);
      setInput('');
    }
  };

  return (
    <div>
      <div className='uploadPDF'></div>
      <div className="chat-container">
      <div className="chat-box">
        {messages.map((message, index) => (
          <div key={index} className={`chat-bubble ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      <div className="input-box">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          
          placeholder="Type a message..."
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
    </div>
    
  );
}

export default Home;
