import React, { useState, useCallback } from 'react';

const MCQPage = () => {
  const [questions, setQuestions] = useState([]);
  const [error, setError] = useState(null);
  const [numQuestions, setNumQuestions] = useState('');
  const [subject, setSubject] = useState('');
  const [selectedOption, setSelectedOption] = useState(null); // Track selected option
  const [feedback, setFeedback] = useState(''); // Track feedback for correctness

  const handleResponse = useCallback((data) => {
    const formattedQuestions = Object.values(data).map(q => ({
      question: q.mcq,
      options: Object.entries(q.options).map(([key, value]) => ({
        key,
        value
      })),
      correct: q.correct // Store correct answer key
    }));
    setQuestions(formattedQuestions);
  }, []);

  const handleFetchQuestions = useCallback(() => {
    fetch('http://127.0.0.1:5000/questions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        numQuestions,
        subject,
      }),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      return response.json();
    })
    .then(data => {
      handleResponse(data); // Process the response data
    })
    .catch(error => {
      setError(error.message);
      console.log("Error fetching questions:", error);
    });
  }, [numQuestions, subject, handleResponse]);

  const handleOptionClick = useCallback((questionIndex, optionKey) => {
    const selectedQuestion = questions[questionIndex];
    const isCorrect = optionKey === selectedQuestion.correct;
    setSelectedOption({ questionIndex, optionKey });
    setFeedback(isCorrect ? 'Correct!' : 'Incorrect. Try again.');
  }, [questions]);

  return (
    <div>
      <input
        type="text"
        value={numQuestions}
        onChange={(e) => setNumQuestions(e.target.value)}
        placeholder="Enter number of questions"
      />
      <input
        type="text"
        value={subject}
        onChange={(e) => setSubject(e.target.value)}
        placeholder="Enter subject of questions"
      />
      <button onClick={handleFetchQuestions}>Fetch Questions</button>

      {error && <p>Error: {error}</p>}
      {questions.length === 0 ? (
        <p>Loading questions...</p>
      ) : (
        questions.map((q, questionIndex) => (
          <div key={questionIndex}>
            <h3>{q.question}</h3>
            <div>
              {q.options.map(option => (
                <button
                  key={option.key}
                  onClick={() => handleOptionClick(questionIndex, option.key)}
                >
                  {option.key}: {option.value}
                </button>
              ))}
            </div>
            {/* Display feedback for the selected option */}
            {selectedOption && selectedOption.questionIndex === questionIndex && (
              <p>{feedback}</p>
            )}
          </div>
        ))
      )}
    </div>
  );
};
export default MCQPage
