import React, { useEffect, useState } from 'react'

const MCQPage = () => {
  const [question, setQuestion] = useState([])
  const [error, setError] = useState(null);
  const [numQuestions, setNumQuestions] = useState('');
  const [subject, setSubject] = useState('');

  const handleResponse = (data) => {
    const formattedQuestions = Object.values(data).map(q => ({
      question: q.mcq,
      options: Object.entries(q.options).map(([key, value]) => ({
        key,
        value
      })),
      correct: q.correct
    }));
    setQuestion(formattedQuestions);
  }

  const handleFetchQuestion = () => {
    fetch('http://127.0.0.1:5000/question', {
      method:"POST",
      headers:{
        'Content-type': 'application/json',
      }, 
      body: JSON.stringify({
        numQuestions,
        subject,
      }),
    }) 
    .then(response => {
      if (!response.ok) {
        throw new error("network response not ok")
      } 
      return response.json();
    }) 
    .then(data => {
      handleResponse(data);
    })
    .catch(error => {
      setError(error.message)
      console.log("Error fetching question: ", error)
    })
  }
  const [selectedOption, setSelectedOption] = useState(null)
  const [feedback, setFeedback] = useState('')
  const handleOptionClick = (questionIndex, optionKey) => {
    const selectedOption = optionKey;
    const correct = optionKey === selectedOption
    setSelectedOption({questionIndex, optionKey})
    setFeedback(correct ? "Correct!" : "It is not correct!")
  }

  
  return (
    <div>
      <input type="text" 
      name=""
       id=""
       value={numQuestions}
       onChange={(e) => setNumQuestions(e.target.value)} 
       placeholder='Enter number of question'/>
      <input type="text" 
      name="" 
      id="" 
      value={subject}
      onChange={(e) => setSubject(e.target.value)}
      placeholder='Enter subject of questions'/>
      <button 
      onClick={handleFetchQuestion}>upload</button>
      {error && <p> Error: {error}</p>} 
      {question.length === 0 ? (
        <p>Loading question...</p>
      ) : (
        question.map((q, index) => (
        <div key={index}>
          <h3>{index + 1}) {q.question}</h3>
          <ul>
            {q.options.map(option => (
              <button key={option.key} onClick={handleOptionClick(index, option.key)}>{option.value}</button>
              
            ))}
          </ul>
          <div>
            {/* {selectedOption && selectedOption.index === index && (
              <p>{feedback}</p>
            )} */}
            {q.correct}
          </div>
        </div>
      ))
      )}
    </div>
  );
}
export default MCQPage;
