import React, { useState } from 'react';



const Home = () => {

  // get set function using useState function!
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');

  const handleSend = async () => {
    if (input.trim() !== '') {
      setMessages([...messages, { text: input, sender: 'user' }]);
      // Simulate a response from the "AI"
      try {
        const response = await fetch('/Home', {
          method: 'GET',
          
        });

        const data = await response.json();

        if (response.ok) {
          const botMessage = {text: data.response, sender: 'bot'}

          setMessages(prevMessage => [...prevMessage, botMessage]);

        } else {
          const errorMessage = {text: 'error: ' + data.error, sender: 'bot'}
        }
      } catch (error) {
        const errorMessage = {text: 'network Error', }
        setMessages(prevMessage => [...prevMessage, errorMessage])
      }
      
      setInput('');
    }
  };

  return (
    <div>
      <div className='uploadPDF'></div>
      <div className="chat-container">
      <div className="chat-box">
        {messages.map((message, index) => (
          <div key={index} className={`chat-bubble ${message.sender}`}>
            {message.text}
          </div>
        ))}
      </div>
      <div className="input-box">
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          
          placeholder="Type a message..."
        />
        <button onClick={handleSend}>Send</button>
      </div>
    </div>
    </div>
    
  );
}

export default Home;
