import React from 'react'
import { useState, useEffect } from 'react'

// useEffect is used for fetching data in realtime while components is running
const Home = () => {
  const [message, setMessage] = useState('');
  const [input, setInput] = useState(' ');

  const handleSend = () => {
    // trim() function is use to remove both end whitespace of a string!

    if (input.trim()) {
      setMessage([...message])
    }
  }
  useEffect(() => {
    fetch('http://127.0.0.1:5000/')
    .then(response => response.json()) 
    .then(data=>setMessage(data.message));
  }, []); 

  return (
    <div>{message}</div>
  )
}

export default Home;
