import React, { useEffect, useState } from 'react'

const MCQPage = () => {
  const [question, setQuestion] = useState([])
  const [error, setError] = useState(null);
  const [numQuestions, setNumQuestion] = useState('');
  const [subject, setSubject] = useState('');

  const handleResponse = (data) => {
    const formattedQuestions = Object.values(data).map(q => ({
      question: q.mcq,
      options: Object.entries(q.options).map(([key, value]) => ({
        key,
        value
      })),
      correct: q.correct
    }));
    setQuestions(formattedQuestions);
  }

  const handleFetchQuestion = () => {
    fetch('http://127.0.0.1:5000/question', {
      method:"POST",
      headers:{
        'Content-type': 'application/json',
      }, 
      body: JSON.stringify({
        numQuestions,
        subject,
      }),
    }) 
    .then(response => {
      if (!response.ok) {
        throw new error("network response not ok")
      } 
      return response.json();
    }) 
    .then(data => {
      handleResponse(data);
    })
    .catch(error => {
      setError(error.message)
      console.log("Error fetching question: ", error)
    })
  }

  
  return (
    <div>
      <input type="text" 
      name=""
       id=""
       value={numQuestions}
       onChange={(e) => setNumQuestion(e.target.value)} 
       placeholder='Enter number of question'/>
      <input type="text" 
      name="" 
      id="" 
      value={subject}
      onChange={(e) => setSubject(e.target.value)}
      placeholder='Enter subject of questions'/>
      <button 
      onClick={handleFetchQuestion}>upload</button>
      {error && <p> Error: {error}</p>} 
      {question.length === 0 ? (
        <p>Loading question...</p>
      ) : (
        question.map((q, index) => (
        <div key={index}>
          <h3>{q.question}</h3>
          <ul>
            {q.options.map((option, i) => (
              <li key={i}>{option}</li>
            ))}
          </ul>
        </div>
      ))
      )}
    </div>
  );
}
export default MCQPage;
